   ```javascript
   const { spawn } = require('child_process');
   const { getConfig } = require('../utils/config-manager');
   const logger = require('../utils/logger');

   async function checkClaudeInstallation() {
     return new Promise((resolve) => {
       const process = spawn('claude', ['--version']);
       process.on('error', () => resolve(false));
       process.on('close', (code) => resolve(code === 0));
     });
   }

   async function pingClaude() {
     const config = getConfig();
     const question = config.question;

     return new Promise((resolve, reject) => {
       logger.info('Starting Claude session...');

       const claudeProcess = spawn('claude', [], { stdio: ['pipe', 'pipe', 'pipe'] });
       let responseReceived = false;
       let timeout;

       // Set timeout
       timeout = setTimeout(() => {
         if (!responseReceived) {
           claudeProcess.kill();
           reject(new Error('Claude response timeout'));
         }
       }, config.timeout);

       claudeProcess.stdout.on('data', (data) => {
         const output = data.toString();
         if (output.includes('Human:')) {
           logger.info(`Human: ${question}`);
           claudeProcess.stdin.write(`${question}\n`);
         } else if (output.includes('Claude:')) {
           logger.info(`Claude: ${output.split('Claude:')[1].trim()}`);
           responseReceived = true;
           claudeProcess.stdin.write('exit\n');
           clearTimeout(timeout);
           resolve(true);
         }
       });

       claudeProcess.on('error', (error) => {
         clearTimeout(timeout);
         reject(error);
       });

       claudeProcess.on('close', (code) => {
         clearTimeout(timeout);
         if (!responseReceived) {
           reject(new Error(`Claude process exited with code ${code}`));
         }
       });
     });
   }

   module.exports = {
     checkClaudeInstallation,
     pingClaude
   };
   ```
