1. Create the start command implementation in lib/commands/start.js:
   ```javascript
   const { fork } = require('child_process');
   const path = require('path');
   const { getConfig, updateConfig } = require('../utils/config-manager');
   const { savePid, getPid, isRunning } = require('../services/process-manager');
   const { checkAutoStartService, setupAutoStart } = require('../services/auto-start-service');
   const inquirer = require('inquirer');
   const logger = require('../utils/logger');

   async function startDaemon(options) {
     // Validate options
     const interval = parseInt(options.interval);
     if (isNaN(interval) || interval < 1 || interval > 24) {
       throw new Error('Interval must be between 1 and 24 hours');
     }

     const retryCount = options.retry ? parseInt(options.retryCount) : 0;
     if (isNaN(retryCount) || retryCount < 0 || retryCount > 10) {
       throw new Error('Retry count must be between 0 and 10');
     }

     const retryInterval = parseInt(options.retryInterval);
     if (isNaN(retryInterval) || retryInterval < 1 || retryInterval > 10) {
       throw new Error('Retry interval must be between 1 and 10 minutes');
     }

     // Update config with command options
     updateConfig({
       intervalHours: interval,
       retryCount: options.retry ? retryCount : 0,
       retryInterval: retryInterval
     });

     // Check if already running
     const pid = getPid();
     if (pid && isRunning(pid)) {
       logger.info(`Claude Ping is already running (PID: ${pid})`);
       return;
     }

     // Check auto-start service
     const isAutoStartSetup = await checkAutoStartService();
     if (!isAutoStartSetup) {
       const { setupService } = await inquirer.prompt([
         {
           type: 'confirm',
           name: 'setupService',
           message: 'Setup auto-start on boot?',
           default: true
         }
       ]);

       if (setupService) {
         await setupAutoStart();
         logger.info('‚úÖ Auto-start service registered');
       }
     }

     // Start in foreground or background
     if (options.foreground) {
       logger.info(`üöÄ Starting Claude Ping (${interval}h interval, ${options.retry ? retryCount : 0} retries)...`);
       require('../services/scheduler').start();
     } else {
       // Fork a new process and detach
       const child = fork(path.join(__dirname, '../services/scheduler.js'), [], {
         detached: true,
         stdio: 'ignore'
       });

       // Save PID and detach
       savePid(child.pid);
       child.unref();

       logger.info(`üöÄ Starting Claude Ping (${interval}h interval, ${options.retry ? retryCount : 0} retries)...`);
       logger.info(`üìÑ PID saved: ${child.pid}`);

       // Calculate next ping time
       const nextPing = new Date(Date.now() + interval * 60 * 60 * 1000);
       logger.info(`‚è∞ Next ping scheduled: ${nextPing.toLocaleString()}`);
       logger.info('\nProcess running in background. Use \'claude-ping status\' to check.');
     }
   }

   module.exports = async function(options) {
     try {
       await startDaemon(options);
     } catch (error) {
       logger.error(`Failed to start: ${error.message}`);
       process.exit(1);
     }
   };
   ```

2. Create the scheduler service in lib/services/scheduler.js:
   ```javascript
   const { getConfig } = require('../utils/config-manager');
   const { pingClaude } = require('./claude-service');
   const { savePid, setupSignalHandlers } = require('./process-manager');
   const logger = require('../utils/logger');

   let mainTimer = null;
   let retryTimer = null;

   async function executeRetrySequence() {
     const config = getConfig();
     const retryCount = config.retryCount;
     const retryInterval = config.retryInterval * 60 * 1000; // convert to ms

     logger.info(`‚è∞ ${config.intervalHours}-hour timer triggered, starting retry sequence...`);

     // Execute first attempt immediately
     let successCount = 0;
     try {
       logger.info(`üîÑ Attempt 1/${retryCount}: Starting Claude session...`);
       await pingClaude();
       logger.info(`‚úÖ Attempt 1 completed`);
       successCount++;
     } catch (error) {
       logger.error(`‚ùå Attempt 1 failed: ${error.message}`);
     }

     // Schedule remaining attempts
     for (let i = 1; i < retryCount; i++) {
       await new Promise(resolve => {
         retryTimer = setTimeout(async () => {
           try {
             logger.info(`üîÑ Attempt ${i+1}/${retryCount}: Starting Claude session...`);
             await pingClaude();
             logger.info(`‚úÖ Attempt ${i+1} completed`);
             successCount++;
           } catch (error) {
             logger.error(`‚ùå Attempt ${i+1} failed: ${error.message}`);
           } finally {
             resolve();
           }
         }, retryInterval);
       });
     }

     logger.info(`üéØ Retry sequence complete. ${successCount}/${retryCount} attempts succeeded.`);
     logger.info(`Next cycle in ${config.intervalHours} hours...`);
   }

   function start() {
     // Save PID if not already saved
     savePid(process.pid);

     // Setup signal handlers
     setupSignalHandlers();

     // Get configuration
     const config = getConfig();
     const intervalMs = config.intervalHours * 60 * 60 * 1000; // convert to ms

     // Execute immediately on start
     if (config.retryCount > 0) {
       executeRetrySequence();
     } else {
       pingClaude().catch(error => {
         logger.error(`Failed to ping Claude: ${error.message}`);
       });
     }

     // Setup main interval timer
     mainTimer = setInterval(() => {
       if (config.retryCount > 0) {
         executeRetrySequence();
       } else {
         pingClaude().catch(error => {
           logger.error(`Failed to ping Claude: ${error.message}`);
         });
       }
     }, intervalMs);
   }

   function stop() {
     if (mainTimer) {
       clearInterval(mainTimer);
       mainTimer = null;
     }

     if (retryTimer) {
       clearTimeout(retryTimer);
       retryTimer = null;
     }
   }

   // Start if this file is executed directly
   if (require.main === module) {
     start();
   }

   module.exports = { start, stop };
   ```
